mapper.select:
	SELECT  COUNT(*)
    FROM    MESSENGER


			WHERE 	MESSENGER_RECEIVER = #{loginUser}
			AND     READ_TM IS NULL
			AND		DELETE_TF = 'N'
			AND		KEEP_TF = 'N'


			WHERE	MESSENGER_RECEIVER = #{loginUser}
			AND		DELETE_TF = 'N'


			WHERE 	MESSENGER_SENDER = #{loginUser}
			AND		DELETE_TF = 'N'


			WHERE 	MESSENGER_RECEIVER = #{loginUser}
			AND     KEEP_TF = 'Y'
			AND		DELETE_TF = 'N'

SELECT * FROM MESSENGER t1 LEFT OUTER JOIN RESIDENT t2 ON (t1.MESSENGER_SENDER = TO_CHAR(t2.RESIDENT_SEQ)) LEFT OUTER JOIN MANAGER t3 ON (t1.MESSENGER_SENDER = t3.MANAGER_SEQ) LEFT OUTER JOIN APT_DETAIL_INFO t4 ON (t2.APT_DETAIL_INFO_SEQ = t4.APT_DETAIL_INFO_SEQ) LEFT OUTER JOIN DEPT t5 ON (t3.DEPT_CD = t5.DEPT_CD)
WHERE MESSENGER_RECEIVER = #{loginUser} AND READ_TM IS NULL AND DELETE_TF = 'N' AND KEEP_TF = 'N'
WHERE MESSENGER_RECEIVER = #{loginUser}
WHERE MESSENGER_SENDER = #{loginUser}
WHERE MESSENGER_RECEIVER = #{loginUser} AND KEEP_TF = 'Y'
AND t1.DELETE_TF = 'N' ORDER BY t1.MESSENGER_ENROLL_DT DESC

mapper.update:
UPDATE	MESSENGER
SET		READ_TM = SYSDATE
WHERE	MESSENGER_SEQ = #{_parameter}

